(async () => {
  let Prompt = (msg.payload || '').trim()
  if (Prompt === '') {
    msg.payload = 'empty or missing prompt'
    node.send([null,msg])
    return
  }

/**** retrieve settings or provide defaults ****/

  let Threads = parseInt(msg.threads,10)
  if (isNaN(Threads)) { Threads = 4 }
  Threads = Math.max(1,Threads)
  Threads = Math.min(Threads,Math.max(1,os.cpus().length))

  let Context = parseInt(msg.context,10)
  if (isNaN(Context)) { Context = 512 }
  Context = Math.max(0,Math.min(Context,2048))

  Prompt = Prompt.replace(/"/g,'\\"')

/**** combine all these settings into a command ****/

  let Command = ( 'cd ai && ' +
    './llama-tokenization --model ./ggml-llama-7b-q4.bin --mlock ' +
    ' --ctx_size ' + Context + 
    ' --threads ' + Threads +
    ' --prompt "' + Prompt + '"'
  )

/**** extract actual reponse from command output ****/

  function ResponseFrom (Text) {
    let HeaderLength = Text.indexOf('system_info')
    Text = Text.slice(HeaderLength + 1)
      .replace(/^[^\n]*\n/,'')

    let TrailerIndex = Text.indexOf('\n\nllama_print_timings')
    Text = Text.slice(0,TrailerIndex)

    return Text
  }

/**** now calculate embeddings for the given prompt ****/

  let { stdout,stderr, StatusCode,Signal } = child_process.spawnSync(
    'bash', [], { input:Command }
  )

  stdout = stdout.toString().trim()
  stderr = stderr.toString().trim()

  switch (true) {
    case (StatusCode == null):
    case (StatusCode === 0):
      msg.statusCode = (stdout === '' ? 204 : 200)
      msg.payload    = ResponseFrom(stdout)
      break
    default:
      msg.statusCode = 500 + StatusCode
      msg.payload    = (stdout === '' ? '' : '>>>> stdout >>>>\n' + stdout + '\n') +
                       '>>>> stderr >>>>\n' + stderr +
                       (Signal == null ? '' : '\n' + Signal)
      break
  }

  node.send([msg,null])
})()
